import React, { useState } from 'react';

import Card   from "../UI/Card";
import Button from "../UI/Button";

import classes  from "./AddUser.module.css";


const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if (+enteredAge < 1 || enteredUsername.trim().length === 0 || setEnteredAge) {
      console.log("Thats not value");
    } else {
      console.log(`submitting form with ${enteredUsername} of ${enteredAge} years old`)
      setEnteredUsername("");
      setEnteredAge("");
    };

  }

  const usernameChangeHandler = (event) => {
    console.log(event.target.value);
    setEnteredUsername(event.target.value);
  }

  const ageChangeHandler = (event) => {
    console.log(event.target.value)
    setEnteredAge(event.target.value);
  }

  return (
    <Card className={classes.input}>
      <form onSubmit={formSubmitHandler}>
        <label>Username</label>
        <input type="text" onChange={usernameChangeHandler} value={enteredUsername}/>
        <label>Age (years)</label>
        <input type="number" step="1" max="110"onChange={ageChangeHandler} value={enteredAge}/>

        <Button type="submit">Add User</Button>
      </form>
    </Card>

  );
}

export default AddUser;
